import React, { useState } from "react";
import '../ExpenseForm/ExpenseForm.css'

function ExpenseForm(){

  function checkUserIntput(e){
    const {name, value} = e.target;
    console.log(name, value)
  }

return (


   

        <form>
            <div className='new-expense__controls'>
            <div className='new-expense__control'>
                <label>Title</label>
                <input onChange={checkUserIntput} type='text' />
                </div>
                <div className='new-expense__control'>
                <label>Amount</label>
                <input onChange={checkUserIntput} type='number' min='0.01' step='0.01'/>
                </div> 
                <div className='new-expense__control'>
                <label>Date</label>
                <input onChange={checkUserIntput} type='date' min='2020-01-01' max='2023-12-01'/>
                </div> 
            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Add Expense</button>
            </div>
        </form>

)
}

export default ExpenseForm;
