import ExpenseForm from "./ExpenseForm";
import "../ExpenseForm/NewExpense.css";
import { useState } from "react";

function NewExpense(props) {

   const [expenseControl, displayExpenseControl] = useState()

    function addNewExpenseBtn(){
        displayExpenseControl(
            <div className="new-expense">
            <ExpenseForm pullData={props.pullData} />
          </div>
        )
    }

  return (
    <>
      <div className="new-expense">

        <div className="new-expense__actions btn--center">
          <button onClick={addNewExpenseBtn} className="new-expense__add-expense-btn">
            Add New Expense
          </button>
        </div>

      </div>


      {expenseControl}
      


    </>
  );
}

export default NewExpense;
