import '../ExpenseForm/ExpenseForm.css'

function ExpenseForm(){

    let [enteredText, setText] = useState('');
    let [enteredAmount, setAmount] = useState('');
    let [enteredDate, setDate] = useState('');

    let textChange = (e) => setText(enteredText = e.target.value)
    let amountChange = (e) => setAmount(enteredAmount = e.target.value)
    let dateChange = (e) => setDate(enteredDate = e.target.value)

return (


   

        <form>
            <div className='new-expense__controls'>
            <div className='new-expense__control'>
                <label>Title</label>
                <input onChange={textChange} type='text' />
                </div>
                <div className='new-expense__control'>
                <label>Amount</label>
                <input onChange={amountChange} type='number' min='0.01' step='0.01'/>
                </div> 
                <div className='new-expense__control'>
                <label>Date</label>
                <input onChange={dateChange} type='date' min='2020-01-01' max='2023-12-01'/>
                </div> 
            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Add Expense</button>
            </div>
        </form>

)
}

export default ExpenseForm;
