import React, { useState } from "react";
import "../ExpenseForm/ExpenseForm.css";

function ExpenseForm(props) {
  const [data, setData] = useState({
    title: "",
    amount: "",
  });

  const [date, setDate] = useState(new Date(2023, 8, 12));

  function checkUserIntput(e) {
    const { name, value } = e.target;
    // console.log(name, value)
    setData((prev) => ({ ...prev, [name]: value }));
  }

  // on pressing submit button
  function submitHandler(e) {
    e.preventDefault();

    props.pullData({ ...data, date: new Date(date) });

    setDate("");

    setData({
      title: "",
      amount: "",
    });
  }

  const dateHandler = (e) => {
    setDate(e.target.value);
  };



  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            onChange={checkUserIntput}
            name="title"
            value={data.title}
            type="text"
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            onChange={checkUserIntput}
            name="amount"
            value={data.amount}
            type="number"
            min="0.01"
            step="0.01"
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            onChange={dateHandler}
            name="date"
            value={date}
            type="date"
            min="2020-01-01"
            max="2023-12-01"
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button onClick={props.cancelFormFunc} style={{marginBottom: '1rem', marginTop: '1rem'}}>Cancel</button>
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}

export default ExpenseForm;
